	/**************** 获取sgrdb pms库连接 ***********************/
	public static Connection getConnect() {
		Connection con = null;
		
		 InputStream inputStream = JDBCHelper.class.getClassLoader().getResourceAsStream("jdbc-pms.properties");
         Properties properties = new Properties();
         try {
			properties.load(inputStream);
		} catch (IOException e1) {
			e1.printStackTrace();
		}
         
		String Url = properties.getProperty("url");
		String name = properties.getProperty("username");
		String psd = properties.getProperty("password");
		String jdbcName = properties.getProperty("driverClassName");
		try {
			Class.forName(jdbcName);
			con = DriverManager.getConnection(Url, name, psd);
		} catch (Exception e) {
			e.printStackTrace();
		}
		return con;
	}

