	/******
	 * 将ResultSetMetaData获取的列名封装到list中
	 * 
	 * @throws SQLException
	 ******/
	public static List<String> queryName2List(ResultSetMetaData data) throws SQLException {
		List<String> list = new ArrayList<String>();
		try {
			int count = data.getColumnCount();
			for (int i = 1; i <= count; i++) {
				list.add(data.getColumnName(i));
			}
		} catch (Exception e) {
			e.printStackTrace();
		}
		return list;
	}

	/****************
	 * 添加数据
	 * 
	 * @throws SQLException
	 ***********************/
	public static void insertStatisticsData(int ruleid, String statisticstype, String statisticsvalue,
			String groupcolumn) {
		String sql = "insert into t_rule_datastatistics (ruleid,statisticstype,statisticsvalue,groupcolumn) values(?,?,?,?)";
		Connection con = getConnect2();
		PreparedStatement pst = null;
		try {
			pst = con.prepareStatement(sql);
			pst.setInt(1, ruleid);
			pst.setString(2, statisticstype);
			pst.setString(3, statisticsvalue);
			pst.setString(4, groupcolumn);
			pst.executeUpdate();
		} catch (Exception e) {
			e.printStackTrace();
		} finally {
			try {
				con.close();
				pst.close();
			} catch (SQLException e) {
				e.printStackTrace();
			}
		}

	}

	/********* 获取ruleid与columnname的对应关系 *********/
	public static Map<String, Integer> queryRuleData(String name) {
		String sql = "select ruleid,columnname,columntype from t_rule_data where columntype like '%NUMBER%' and tname=?";
		Map<String, Integer> map = null;
		PreparedStatement rule_data = null;
		Connection con = null;
		try {
			con = getConnect2();
			rule_data = con.prepareStatement(sql);
			rule_data.setString(1, name);
			ResultSet rs = rule_data.executeQuery();
			map = new HashMap<String, Integer>();
			while (rs.next()) {
				map.put(rs.getString(2), rs.getInt(1));
			}
		} catch (Exception e) {
			e.printStackTrace();
		} finally {
			try {
				con.close();
				rule_data.close();
			} catch (SQLException e) {
				e.printStackTrace();
			}

		}

		return map;
	}

	/*************
	 * 转换和清洗数据
	 * 
	 * @throws SQLException
	 ****************/
	public static void cleanData(ResultSet query, Map<String, Integer> map, List<String> list) throws SQLException {
		/******** 定义数据变量 ********/
		Integer ruleID = null;
		String statisticstype = null;
		String statisticsvalue = null;
		String groupcolumn = null;
		/********** 根据列名分类数据 ***************/
		while (query.next()) {
			for (String key : map.keySet()) {
				for (int j = 0; j < list.size(); j++) {
					// 是否在map中，即所查的表列字段是否在总表中。
					boolean IsExit = list.get(j).contains(key);
					if (IsExit) {
						/***** 列名中包含MAX字段 ****/
						if (list.get(j).toUpperCase().contains("_MAX")) {
							statisticstype = "max";
							/***** 列名中包含MIN字段 ****/
						} else if (list.get(j).toUpperCase().contains("_MIN")) {
							statisticstype = "min";
							/***** 列名中包含null_num字段 ****/
						} else if (list.get(j).contains("_null_num")) {
							statisticstype = "null_num";
						}
						statisticsvalue = query.getString(list.get(j));
						String sccj = query.getString(1);
						String xh = query.getString(3);
						// 非空才入库
						if (statisticsvalue != null && !statisticsvalue.equals("") && sccj != null & xh != null) {
							ruleID = map.get(key);
							groupcolumn = "sccj=" + sccj + "," + "xh=" + xh;
							insertStatisticsData(ruleID, statisticstype, statisticsvalue, groupcolumn);
						}
					}

				}
			}
		}
	}

	/*************** 封装sql语句和查询名称 **************/
	public static void cleanAndTranslateData(String sql, String name) {
		List<String> rowList_tou = null;
		Connection con = null;
		PreparedStatement sql_data = null;
		try {
			con = getConnect();
			sql_data = con.prepareStatement(sql);
			ResultSet executeQuery = sql_data.executeQuery();
			executeQuery.last();
			int row = executeQuery.getRow();
			executeQuery.first();
			if (row < 1) {
				return;
			}
			ResultSetMetaData data = executeQuery.getMetaData();
			rowList_tou = queryName2List(data);
			Map<String, Integer> map = queryRuleData(name);
			cleanData(executeQuery, map, rowList_tou);
		} catch (Exception e) {
			e.printStackTrace();
		} finally {
			try {
				con.close();
				sql_data.close();
			} catch (SQLException e) {
				e.printStackTrace();
			}
		}
	}

